import React, { useState } from "react";
import RedirectAcount from "./Redirect_Acount";

const Options = (props) => {
  const [login, setLogin] = useState(false);
  return (
    <>
      <div className="flex justify-between flex-col rounded-xl shadow-lg border-1 border-red-500 h-60 w-80 m-4">
        <h2 className="m-3 text-center font-semibold text-xl font-sans">
          {props.title}
        </h2>
        {login ? 
          <RedirectAcount />
         : 
          <a
            href="/acceder"
            className="text-blue-400 m-3 text-center cursor-pointer"
          >
            ACCEDE A TU CUENTA
          </a>
        }
      </div>
    </>
  );
};

export default Options;
