import React, { useState } from "react";

const Options = props => {
  const [login, setLogin] = useState(false);
  return (
    <>
      <div className="rounded-xl shadow-lg border-1 border-red-500 h-60 w-80 m-4">
        <h2 className="m-3 text-center font-semibold text-xl font-sans">
          {props.title}
        </h2>
        {login ? 
          <a href="/cuenta" className="text-blue-400">
            ACCEDE A TU CUENTA
          </a>
         : 
          <a href="/acceder" className="text-blue-400 ml-4">
            ACCEDE A TU CUENTA
          </a>
        }
      </div>
    </>
  );
};

export default Options;
