import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../../components/navbar";

const Catalogo = () => {
  const [catalog, setCatalog] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    fetch(`https://series-films.netlify.app/datas/${id}.json`)
      .then((res) => res.json())
      .then((data) => setCatalog(data));
  }, []);

  console.log(catalog.seasons?.chapters);

  catalog.seasons?.chapters.map((e) => {
    console.log(e.title);
  });

  return (
    <div className="pb-20">
      <Navbar />
      <div
        className="h-64 sm:h-80 lg:h-128 bg-cover bg-bottom"
        style={{
          backgroundImage: `url("/images/main-image.jpg")`,
        }}
      ></div>
      <h1 className="mt-4">
        CAT√ÅLOGO - <span>{catalog.title}</span>
      </h1>

      {catalog.seasons?.chapters.map((chapter) => {
        return (
          <>
            <ul>
              <li>
                <a>{chapter.title}</a>
              </li>
            </ul>
          </>
        );
      })}
      {/* {
        catalog !== {} && (
          Object.keys(catalog.seasons.chapters).foreach((season) => {
            <>
              <p>{season.season}</p>
              <ul>
                {season.map((chapter) => {
                  <li>
                    <a target={"_blank"} rel="noreferrer" href={chapter.link}>
                      {chapter.title}
                    </a>
                  </li>;
                })}
              </ul>
            </>;
          })
        )
      } */}
      <a
        target={"_blank"}
        rel="noreferrer"
        href="https://drive.google.com/file/d/1hKsKrIRukcfdwW0wFKrFDaRvcKi3foFi/view?usp=sharing"
      >
        Capitulo 1
      </a>
    </div>
  );
};

export default Catalogo;
