import React, { useState } from "react";
import RedirectAcount from "./redirectAcount";

const Options = (props) => {
  const [login, setLogin] = useState(false);
  return (
    <>
      <div
        className="flex justify-between flex-col rounded-xl shadow-lg border-1 border-red-500 h-60 w-80 m-4"
        style={{ backgroundImage: `url(${props.image})`, backgroundRepeat: 'none', backgroundPosition: 'center', backgroundSize:'center' }}
      >
        <h2 className="m-3 text-center font-semibold text-xl font-sans">
          {props.title}
        </h2>
        {login ? (
          <RedirectAcount text="ACCEDE A TU CUENTA" link="/cuenta" />
        ) : (
          <RedirectAcount text="ACCEDE A TU CUENTA" link="/acceder" />
        )}
      </div>
    </>
  );
};

export default Options;
