import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../../components/navbar";

const Catalogo = () => {
  const [catalog, setCatalog] = useState({});
  const { id } = useParams();

  useEffect(() => {
    fetch(`https://series-films.netlify.app/datas/${id}.json`)
      .then((res) => res.json())
      .then((data) => setCatalog(data));
  }, []);

  console.log(catalog);

  return (
    <div className="m-4">
      <Navbar />
      <h1 className="mt-4">
        CAT√ÅLOGO - <span>{catalog.title}</span>
      </h1>
      {
        catalog !== {} && (
          JSON.stringify(catalog.seasons).map((season) => {
            <>
              <p>{season.season}</p>
              <ul>
                {season.map((chapter) => {
                  <li>
                    <a target={"_blank"} rel="noreferrer" href={chapter.link}>
                      {chapter.title}
                    </a>
                  </li>;
                })}
              </ul>
            </>;
          })
        )
      }
      <a
        target={"_blank"}
        rel="noreferrer"
        href="https://drive.google.com/file/d/1hKsKrIRukcfdwW0wFKrFDaRvcKi3foFi/view?usp=sharing"
      >
        Capitulo 1
      </a>
    </div>
  );
};

export default Catalogo;
