import React from "react";

const RedirectAcount = (props) => {
  return (
    <a
      href={props.link}
      className="text-blue-800 m-3 text-center cursor-pointer font-bold"
    >
      <div
        className="flex justify-between flex-col rounded-xl shadow-xl border-1 border-red-500 h-60 w-80 p-2 cursor-pointer"
        style={{
          backgroundImage: `url(${props.image})`,
          backgroundRepeat: "none",
          backgroundPosition: "center",
          backgroundSize: "cover",
          backgroundColor: 'rgba(255,255,255,0.6)',
          backgroundBlendMode: 'overlay'
        }}
      >
        <h2 className="m-3 text-center font-semibold text-xl font-sans text-black">
          {props.title}
        </h2>
        {props.text}
      </div>
    </a>
  );
};

export default RedirectAcount;
